/**
 * Supported super-router request methods.
 */
module.exports = {
  GET     : 'GET',
  PUT     : 'PUT',
  POST    : 'POST',
  PATCH   : 'PATCH',
  DELETE  : 'DELETE',
  HEAD    : 'HEAD',
  OPTIONS : 'OPTIONS',
  ALL     : '*'
};

'use strict';
const _           = require('lodash');
const utils       = require('./utils');
const Route       = require('./Route');
const METHOODS    = require('./METHODS');
const RouteParser = require('route-parser');

/**
 * Router Node.
 * Represents a unique path on the router. Each node may contain one route per method, and any number of child nodes.
 * Nodes build a linked tree structure, representing each unique route on the router. It is used to serve OPTIONS
 * requests.
 */
class Node {

  /**
   * Node constructor.
   *
   * Creating a node automatically registers the OPTIONS request on this path.
   *
   * @param {Object} options - The input options
   * @param {String} options.path - The path pattern the node represents
   * @returns {Node} - The Node instance
   */
  constructor(options) {
    if (!_.isObject(options)) {
      throw new TypeError('options must be an object.');
    }
    if (!_.isString(options.path)) {
      throw new TypeError('path must be a string.');
    }
    if (!/^\//.test(options.path)) {
      throw new TypeError('path must start with a / character.');
    }

    this._path     = utils.normalizePath(options.path);
    this._parsed   = new RouteParser(this._path);
    this._routes   = new Map();
    this._children = new Map();

    this.addRoute({
      path    : this.path,
      method  : METHOODS.OPTIONS,
      handler : (options) => {
        const response = options.response;

        response.setBody(this.toObject());
      }
    });
  }

  /**
   * Gets the path pattern that the node represents.
   *
   * @returns {String} - The path
   */
  get path() {
    return this._path;
  }

  /**
   * Parses the given path string against this node's path pattern. If the input path is a match, returns
   * an object whose key value pairs are the matched route params and their values. If the path is not a match,
   * returns false.
   *
   * @param {String} path - The input path
   * @returns {Boolean|Object} - False if the path is not a match, an object if it is.
   * @private
   */
  _parsePath(path) {
    return this._parsed.match(path);
  }

  /**
   * Adds a route to the node. Will throw an error if the route does not match the path of the node, if there is
   * already a route registered on the given method, or if the route method is ALL (routes on a node must be
   * deterministic).
   *
   * @param {Route} route - The route to add to the node.
   * @throws TypeError - On validation error.
   * @returns {void}
   */
  addRoute(route) {
    if (!(route instanceof Route)) {
      route = new Route(route);
    }
    if (!this.isSame(route)) {
      throw new TypeError('route path must match node path.');
    }
    if (route.method === METHOODS.ALL) {
      throw new TypeError('cannot register route with method ALL on node.');
    }
    if (this._routes.has(route.method)) {
      throw new TypeError(`duplicate method "${route.method}" added for path "${route.path}"`);
    }
    this._routes.set(route.method, route);
  }

  /**
   * Adds a child node to this node.
   *
   * @param {Node} node - The node to add as a child.
   * @returns {void}
   */
  addChild(node) {
    if (!(node instanceof Node)) {
      node = new Node(node);
    }
    if (this._children.has(node.path)) {
      throw new TypeError(`Cannot add duplicate child on path ${node.path}`);
    }

    this._children.set(node.path, node);
  }

  /**
   * Returns the node's children as an array.
   *
   * @returns {Array<Node>} - The node's children.
   */
  getChildren() {
    return Array.from(this._children.values());
  }

  /**
   * Returns the node's routes as an array.
   *
   * @returns {Array<Route>} - The routes associated with this node.
   */
  getRoutes() {
    return Array.from(this._routes.values());
  }

  /**
   * Indicates if this node is a parent to a route by comparing their paths.
   *
   * @param {Route} route - The route to compare.
   * @returns {boolean} - If this node is a parent to the input route.
   */
  isParent(route) {
    return utils.comparePaths(route.path, this.path) === 1;
  }

  /**
   * Indicates if this node represents the same path as a route.
   *
   * @param {Route} route - The route to compare.
   * @returns {boolean} - If this node is the same path as the input route.
   */
  isSame(route) {
    return utils.comparePaths(route.path, this.path) === 0;
  }

  /**
   * Indicates if the node can insert a route. Returns true if the node is the same or a parent to the route.
   *
   * @param {Route} route - The route to compare.
   * @returns {boolean} - If the route can be inserted on this node.
   */
  canInsert(route) {
    return utils.comparePaths(route.path, this.path) !== -1;
  }

  /**
   * Recursively inserts a route into the node.
   * If the node is not the same or a parent to the route, has no effect.
   * If the route is on the same path as the node, adds the route to this node.
   * If the route can be inserted on a child node, recurses down to the child node.
   * Otherwise, adds a new child node to this node, then adds the route to the child node.
   *
   * @param {Route} route - The route to insert.
   * @returns {void}
   */
  insert(route) {
    if (!(route instanceof Route)) {
      route = new Route(route);
    }

    if (!(this.canInsert(route))) {
      throw new TypeError(`Cannot insert "${route.path}" into "${this.path}". It is not a match or a child path.`);
    }

    if (this.isSame(route)) {
      return this.addRoute(route);
    }

    for (const child of this._children.values()) {
      if (child.canInsert(route)) {
        return child.insert(route);
      }
    }

    const newNode = new Node({ path : route.path });
    newNode.addRoute(route);
    this._children.forEach((child, path) => {
      if (newNode.isParent(child)) {
        this._children.delete(path);
        newNode.addChild(child);
      }
    });
    this.addChild(newNode);
  }

  /**
   * Recursively finds a route that matches the specified request. Returns a matching route or null if not match
   * can be found.
   *
   * @param {Request} request - The request to find a matching route against
   * @returns {Route} - The matching route.
   */
  find(request) {
    let match;
    if (this._parsePath(request.path)) {
      match = this._routes.get(request.method);
      if (match) {
        return match;
      }
    }

    for (const child of this._children.values()) {
      match = child.find(request);
      if (match) {
        return match;
      }
    }

    return null;
  }

  /**
   * Returns a pojo representation of the node.
   *
   * @returns {Object} - Plain object representation of the node.
   */
  toObject() {
    const pojo = {
      methods     : _.map(this.getRoutes(), (route) => {
        return route.method;
      }),
      childRoutes : _.map(this.getChildren(), (childNode) => {
        return childNode.path;
      })
    };
    return pojo;
  }
}

module.exports = Node;

'use strict';
const _                = require('lodash');
const utils            = require('./utils');
const TransformStream  = require('stream').Transform;
const isReadableStream = require('isstream').isReadable;

/**
 * SuperRouter Request object.
 */
class Request {
  /**
   * Constructs a new request object.
   *
   * @param {object} options - The options object
   * @param {string} path - The request path
   * @param {string} method - The request method
   * @param {object} [headers={}] - The request headers
   * @param {ReadableStream} [body] - The request body stream
   * @returns {Request} Request object
   */
  constructor(options) {
    if (!_.isObject(options)) {
      throw new TypeError('options must be an object.');
    }

    const headers = options.headers;
    const body    = options.body;

    if (headers != null && !_.isObject(headers)) {
      throw new TypeError('headers must be an object.');
    }
    if (body != null && !isReadableStream(body)) {
      throw new TypeError('body must be a readable stream.');
    }

    this.path   = options.path;
    this.method = options.method;

    this._originalPath    = options.originalPath || options.path;
    this._headers         = new Map();
    _.each(headers, (v, k) => {
      this._headers.set(k.toLowerCase(), v);
    });
    this._body            = new TransformStream();
    this._body._transform = function (chunk, encoding, done) {
      return done(null, chunk);
    };
    if (isReadableStream(body)) {
      body.pipe(this._body);
    }

    _.each(options, (v, k) => {
      if (_.isUndefined(this[k])) {
        this[k] = v;
      }
    });
  }

  /**
   * Returns the original path of the request. The original path is the path that the request
   * was initialized with, and cannot be changed, even if the path is later updated.
   *
   * @returns {string} - Original path
   */
  get originalPath() {
    return this._originalPath;
  }

  /**
   * Returns the request path
   *
   * @returns {string} - Request path
   */
  get path() {
    return this._path;
  }

  /**
   * Sets the request path.
   *
   * @param {string} path - The new request path
   * @returns {void}
   */
  set path(path) {
    if (!_.isString(path)) {
      throw new TypeError('path must be a string.');
    }
    this._path = utils.normalizePath(path);
  }

  /**
   * Returns the request method. Request method will be lower-cased.
   *
   * @returns {string} - The request method.
   */
  get method() {
    return this._method;
  }

  /**
   * Sets the request method. Input is case-insensitive.
   *
   * @param {string} method - The new request method.
   * @returns {void}
   */
  set method(method) {
    if (!(utils.isValidMethod(method))) {
      throw new TypeError('method must be a valid method string.');
    }
    this._method = utils.normalizeMethod(method);
  }

  /**
   * Returns the request headers as a plain object. Keys and values are lower-cased.
   *
   * @returns {object} - The request headers.
   */
  get headers() {
    const obj = {};
    for (const kv of this._headers) {
      obj[kv[0]] = kv[1];
    }

    return obj;
  }

  /**
   * Returns the value of a request header. Input is case-insensitive.
   *
   * @param {string} key - The request header key
   * @returns {string} - The request header value
   */
  getHeader(key) {
    return this._headers.get(key.toLowerCase());
  }

  /**
   * Returns the underlying request body stream.
   *
   * @returns {TransformStream} - The request body stream.
   */
  get body() {
    return this._body;
  }

  /**
   * Sets the request body value.
   *
   * @param {*} val - The new request body value.
   * @returns {void}
   */
  set body(val) {
    this._body = val;
  }
}

module.exports = Request;

'use strict';
const _                = require('lodash');
const TransformStream  = require('stream').Transform;
const isReadableStream = require('isstream').isReadable;

/**
 * SuperRouter Response object.
 */
class Response {
  constructor() {
    this.setBody();

    this._headers   = new Map();
    this.statusCode = 200;
  }

  /**
   * Gets the status code of the response.
   * @returns {number} - the status code
   */
  get statusCode() {
    return this._statusCode;
  }

  /**
   * Sets the status code of the response.
   * @param {number} val - The new status code
   * @returns {void}
   */
  set statusCode(val) {
    if (!_.isFinite(val)) {
      throw new TypeError('statusCode must be a number.');
    }
    this._statusCode = val;
  }

  /**
   * Gets the headers of the response as a plain object. Response header key / value pairs are all
   * lower-cased.
   * @returns {Object} - Response headers
   */
  get headers() {
    const obj = {};
    for (const kv of this._headers) {
      obj[kv[0]] = kv[1];
    }

    return obj;
  }

  /**
   * Gets the value of a response header. Keys are case-insensitive. Returns undefined if the header
   * is not set.
   * @param {string} key - the header key
   * @returns {string} - the header value.
   */
  getHeader(key) {
    if (!_.isString(key)) {
      throw new TypeError('First argument: key must be a string.');
    }
    return this._headers.get(key.toLowerCase());
  }

  /**
   * Sets the value of a response header. Keys and values are case-insensitive.
   * @param {string} key - the response header key
   * @param {string} value - the response header value
   * @returns {void}
   */
  setHeader(key, value) {
    if (!_.isString(key)) {
      throw new TypeError('First argument: key must be a string.');
    }
    if (!_.isString(value)) {
      throw new TypeError('Second argument: value must be a string.');
    }
    this._headers.set(key.toLowerCase(), value);
  }

  /**
   * Clears the value of a response header.
   * @param {string} key - the response header to clear
   * @returns {void}
   */
  clearHeader(key) {
    if (!_.isString(key)) {
      throw new TypeError('First argument: key must be a string.');
    }
    this._headers.delete(key.toLowerCase());
  }

  /**
   * Returns the underlying response body stream.
   * @returns {TransformStream} - The response body stream.
   */
  get body() {
    return this._body;
  }

  /**
   * Returns the response body according to how it was most recently set. If the body is being
   * used as a stream, will return the body stream. If the body is being written with values, will
   * return the last assigned value.
   * @returns {*} - The response body value
   */
  getBody() {
    return this._lastAssignedBody;
  }

  /**
   * Sets the value of the response body. If the value is a readable stream, it will be piped into
   * the underlying response body stream. If the value is plain type, it will be written to the
   * underlying body stream.
   * @param {ReadableStream|*} body - The new body value
   * @returns {void}
   */
  setBody(body) {
    this._body             = new TransformStream({ objectMode : true });
    this._body._transform  = function (chunk, encoding, done) {
      return done(null, chunk);
    };
    this._lastAssignedBody = this._body;

    if (isReadableStream(body)) {
      body.pipe(this._body);
      body.on('error', (err) => {
        this._body.emit('error', err);
      });
    }
    else if (!_.isUndefined(body)) {
      this._body.end(body);
      this._lastAssignedBody = body;
    }
  }

}

module.exports = Response;

'use strict';
const _           = require('lodash');
const Q           = require('q');
const utils       = require('./utils');
const METHODS     = require('./METHODS');
const Request     = require('./Request');
const Response    = require('./Response');
const RouteParser = require('route-parser');

/**
 * Route class.
 * A route is defined as a path pattern, method, and handler. Testing requests against routes and executing matching
 * handlers is the core of request processing.
 */
class Route {

  /**
   * Instantiates a new Route.
   *
   * @param {Object} options - The options object
   * @param {Function} options.handler - The handler function to be executed if the route is matched.
   * @param {String} [path='*all'] - The path pattern that the route should be executed against. If not provided,
   *  matches all paths.
   * @param {String} [method='*'] - The method that the route should be executed against. If not provided, mathes
   *  all methods.
   * @returns {Route} - The new route instance.
   */
  constructor(options) {
    if (!_.isObject(options)) {
      throw new TypeError('options must be an object.');
    }
    if (!_.isFunction(options.handler)) {
      throw new TypeError('handler must be a function.');
    }
    if (options.path && !_.isString(options.path)) {
      throw new TypeError('path must be a string.');
    }
    if (options.method && !utils.isValidMethod(options.method)) {
      throw new TypeError('method must be a valid method string.');
    }

    _.extend(this, options);

    this._path    = utils.normalizePath(options.path || '*all');
    this._method  = utils.normalizeMethod(options.method || METHODS.ALL);
    this._handler = options.handler;
    this._parsed  = new RouteParser(this._path);
  }

  /**
   * Returns the route path.
   *
   * @returns {String} - The route path.
   */
  get path() {
    return this._path;
  }

  /**
   * Returns the route method.
   *
   * @returns {String} - The route method.
   */
  get method() {
    return this._method;
  }

  /**
   * Returns the route handler.
   *
   * @returns {Function} - The route handler.
   */
  get handler() {
    return this._handler;
  }

  /**
   * Tests if the given method matches this route
   *
   * @param {String} method - The input method
   * @returns {boolean} - If the method matches this route
   * @private
   */
  _isMethodMatch(method) {
    return (this.method === METHODS.ALL) || (this.method === utils.normalizeMethod(method));
  }

  /**
   * Parses the given path string against this route's path pattern. If the input path is a match, returns
   * an object whose key value pairs are the matched route params and their values. If the path is not a match,
   * returns false.
   *
   * @param {String} path - The input path
   * @returns {Boolean|Object} - False if the path is not a match, an object if it is.
   * @private
   */
  _parsePath(path) {
    return this._parsed.match(path);
  }

  /**
   * Tests whether a given request object is a match against this route, satisfying the path & method requirements.
   *
   * @param {Request} request - The input requiest
   * @returns {Boolean} - Whether the request matches this route.
   */
  isMatch(request) {
    if (!(request instanceof Request)) {
      throw new Error('First argument: request must be a SuperRouter Request instance.');
    }
    return this._isMethodMatch(request.method) && _.isObject(this._parsePath(request.path));
  }

  /**
   * Executes this route against a provided request / response. Returns a promise that will resolve or reject when
   * execution is complete.
   *  - If the request is not a match, returns an empty promise.
   *  - If the request is a match, returns a promise for the return value of the route handler
   *
   * @param {Object} options - The options object
   * @param {Request} options.request - The request the route will be tested & executed against
   * @param {Response} options.response - The response the route handler will be invoked with
   * @returns {Promise} - A promise that will complete when execution is complete.
   */
  execute(options) {
    if (!_.isObject(options)) {
      throw new TypeError('options must be an object.');
    }

    const request  = options.request;
    const response = options.response;

    if (!(request instanceof Request)) {
      throw new Error('request must be a SuperRouter Request instance.');
    }
    if (!(response instanceof Response)) {
      throw new Error('response must be a SuperRouter Response instance.');
    }

    request.routeParams = {};
    if (!this.isMatch(request)) {
      return Q();
    }
    try {
      request.routeParams = this._parsePath(request.path);
      return Q(this.handler(options));
    }
    catch (err) {
      return Q.reject(err);
    }
  }

}

module.exports = Route;

'use strict';

const Node  = require('./Node');

class RouteTree {
  constructor() {
    this._root = new Node({ path : '/' });
  }

  addRoute(route) {
    return this._root.insert(route);
  }

  find(request) {
    return this._root.find(request);
  }

}

module.exports = RouteTree;

'use strict';

const Route     = require('./Route');
const RouteTree = require('./RouteTree');

const SPLAT    = /\*/;
const OPTIONAL = /\(/;

/**
 * SuperRouter Router object
 */
class Router {
  constructor() {
    this._routesTree = new RouteTree();

    this.match   = this.match.bind(this);
    this.execute = this.execute.bind(this);
  }

  /**
   * Adds a route to the router. Routes must be unique and deterministic (no splats or optionals). Routes can
   * be annotated with additional information, will be available on the Request after matching.
   *
   * Any route parameters defined in the path will be available on the request.routeParams object when the
   * handler is executed.
   *
   * If the route handler returns a promise, the app will wait for that promise to resolve or reject before
   * moving to the next middleware in the stack.
   *
   * @example
   * router.addRoute({
   *  path : '/cases/:caseId',
   *  method : 'get',
   *  handler : ({request, response}) => {
   *    return Database.getCaseById(request.routeParams.caseId).then((caseInstance) => {
   *      request.setBody(caseInstance);
   *    });
   *  }
   * });
   *
   * @param {Route} route - The route definition. If a plain object is provided, will be run through the
   *   Route constructor.
   * @returns {void}
   */
  addRoute(route) {
    if (!(route instanceof Route)) {
      route = new Route(route);
    }

    if (route.path.match(SPLAT) || route.path.match(OPTIONAL)) {
      throw new TypeError('Splats and optional groups are not supported for routes.');
    }

    this._routesTree.addRoute(route);
  }

  /**
   * Middleware to be consumed by a SuperRouter App instance. Causes a request to be matched against
   * the router. The matched route will be assigned to `request.matchedRoute`. In the case of no match,
   * the middleware does not throw an error; the matchedRoute key will simply be undefined.
   *
   * @example
   * app.use(router.match);
   *
   * @param {object} options - The request & response used by app middleware.
   * @returns {void}
   */
  match(options) {
    const request = options.request;

    request.matchedRoute = this._routesTree.find(request);
  }

  /**
   * Middleware to be consumed by a SuperRouter App instance. Causes the handler of a matched route to
   * be executed. If `request.matchedRoute` is not defined, the middleware does nothing.
   *
   * @example
   * app.use(router.execute);
   *
   * @param {object} options - The request & response used by app middleware.
   * @returns {void}
   */
  execute(options) {
    const request  = options.request;
    const response = options.response;

    if (request.matchedRoute != null) {
      return request.matchedRoute.execute({ request, response });
    }
  }

}

module.exports = Router;

