'use strict';

exports.Request    = require('./Request');
exports.Response   = require('./Response');
exports.Router     = require('./Router');
exports.App        = require('./App');
exports.Middleware = require('./middleware');

'use strict';

const Q = require('q');

class ContentNegotiation {

  static request(opts) {
    const request = opts.request;

    if (_isMultipart(request)) {
      return;
    }

    const deferred = Q.defer();
    let newBody    = null;
    request.body.on('data', (chunk) => {
      try {
        newBody = JSON.parse(chunk);
      }
      catch (e) {
        deferred.reject(new Error('failed to parse json in request'));
      }
    });

    request.body.on('end', () => {
      request.body = newBody;
      deferred.resolve();
    });

    return deferred.promise;
  }

  static response(opts) {
    const response = opts.response;

    response.setHeader('content-type', 'application/json');
    return response.setBody(JSON.stringify(response.getBody()));
  }

}

function _isMultipart(request) {
  const contentType = request.getHeader['Transfer-Encoding'];
  if (contentType && contentType.toLowerCase().indexOf('chunked') > -1) {
    return true;
  }

  return false;
}

module.exports = ContentNegotiation;

'use strict';
const _       = require('lodash');
const METHODS = require('./METHODS');

/**
 * Utility class
 * @private
 */
class utils {

  /**
   * Compares two path pattern strings, as used in routing. Returns 0 if they are the same (they would match
   * the same request path). Returns 1 if the first path is a parent to the second path. Returns -1 if the
   * second path is a parent, sibling, or cousin.
   *
   * @param {String} p1 - first path string
   * @param {String} p2 - first path string
   * @returns {Number} - The comparator value
   */
  static comparePaths(p1, p2) {
    const PARAM_RE = /\/:[^\/]+/gi;
    p1             = p1.replace(PARAM_RE, '/:');
    p2             = p2.replace(PARAM_RE, '/:');

    if (p1 === p2) {
      return 0;
    }
    if (_.startsWith(p1, p2)) {
      return 1;
    }
    return -1;

  }

  /**
   * Normalizes path strings for routing and requests.
   *  - Strips trailing slashes
   *  - Lowercases path parts other than route params
   *
   * @param {String} path - The path string
   * @returns {String} - The normalized path
   */
  static normalizePath(path) {
    while (_.last(path) === '/' && path.length > 1) {
      path = path.slice(0, -1);
    }
    return path.replace(/\/[^:][^\/]+/gi, (match) => {
      return match.toLowerCase();
    });
  }

  /**
   * Normalizes a method string to lower case
   *
   * @param {string} method - The method string
   * @returns {string} - The nrormalized method
   */
  static normalizeMethod(method) {
    return method.toUpperCase();
  }

  /**
   * Tests if a method string is valid.
   *
   * @param {String} method - The method string
   * @returns {Boolean} - If the method is valid
   */
  static isValidMethod(method) {
    return _.isString(method) && _.contains(METHODS, this.normalizeMethod(method));
  }
}

module.exports = utils;

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');
const proxyquire        = require('proxyquire');
const Q                 = require('q');

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect  = chai.expect;
const sandbox = sinon.sandbox.create();

const Request  = require('../lib/Request');
const Response = require('../lib/Response');
const Route    = require('../lib/Route');

let App;
let app;

describe('App', () => {

  beforeEach(() => {
    App = require('../lib/App');
    app = new App();
  });

  describe('constructor', () => {

  });

  describe('use', () => {
    let mockRoute;

    beforeEach(() => {
      mockRoute = sinon.spy();
      App       = proxyquire('../lib/App', {
        './Route' : mockRoute
      });
      app       = new App();
    });


    it('should run the input through the Route constructor if it is not a Route instance', () => {
      const opts = {};
      app.use(opts);
      expect(mockRoute).to.have.been.calledOnce;
      expect(mockRoute).to.have.been.calledWithNew;
      expect(mockRoute).to.have.been.calledWith(opts);
    });

    it('should not run the input through the Route constructor if it is a Route instance', () => {
      const route = new mockRoute({ handler : sinon.spy() });
      mockRoute.reset();

      app.use(route);
      expect(mockRoute).to.not.have.been.called;
    });

    it('should accept a function input and use it as a route handler', () => {
      const opts = sinon.spy();
      app.use(opts);
      expect(mockRoute).to.have.been.calledOnce;
      expect(mockRoute).to.have.been.calledWithNew;
      expect(mockRoute).to.have.been.calledWith({
        handler : opts
      });
    });
  });

  describe('useError', () => {
    let mockRoute;

    beforeEach(() => {
      mockRoute = sinon.spy();
      App       = proxyquire('../lib/App', {
        './Route' : mockRoute
      });
      app       = new App();
    });


    it('should run the input through the Route constructor if it is not a Route instance', () => {
      const opts = {};
      app.useError(opts);
      expect(mockRoute).to.have.been.calledOnce;
      expect(mockRoute).to.have.been.calledWithNew;
      expect(mockRoute).to.have.been.calledWith(opts);
    });

    it('should not run the input through the Route constructor if it is a Route instance', () => {
      const route = new mockRoute({ handler : sinon.spy() });
      mockRoute.reset();

      app.useError(route);
      expect(mockRoute).to.not.have.been.called;
    });

    it('should accept a function input and use it as a route handler', () => {
      const opts = sinon.spy();
      app.useError(opts);
      expect(mockRoute).to.have.been.calledOnce;
      expect(mockRoute).to.have.been.calledWithNew;
      expect(mockRoute).to.have.been.calledWith({
        handler : opts
      });
    });
  });

  describe('processRequest', () => {
    let request;
    let middleware1;
    let middleware2;
    let errMiddleware1;
    let errMiddleware2;

    beforeEach(() => {
      request = new Request({
        headers : {},
        path    : '/a',
        method  : 'get'
      });

      middleware1 = sinon.createStubInstance(Route);
      middleware2 = sinon.createStubInstance(Route);

      errMiddleware1 = new Route({
        handler : sinon.spy()
      });
      sandbox.stub(errMiddleware1, 'execute');
      errMiddleware2 = new Route({
        handler : sinon.spy()
      });
      sandbox.stub(errMiddleware2, 'execute');

      app.use(middleware1);
      app.use(middleware2);

      app.useError(errMiddleware1);
      app.useError(errMiddleware2);
    });

    it('should create a new Request object if the input is not a Request object', () => {
      const mockRequest = sinon.stub();
      const opts        = {};
      App               = proxyquire('../lib/App', {
        './Request' : mockRequest
      });
      app = new App();

      app.processRequest(opts);
      expect(mockRequest).to.have.been.calledOnce;
      expect(mockRequest).to.have.been.calledWith(opts);
    });

    it('should return a promise', () => {
      expect(Q.isPromise(app.processRequest(request))).to.be.true;
    });

    it('should create a new response object', () => {
      const Response     = sinon.stub();
      const mockResponse = {
        pipe : sinon.spy()
      };
      Response.returns(mockResponse);
      App                = proxyquire('../lib/App', {
        './Response' : Response
      });
      app                = new App();

      app.use(middleware1);
      app.use(middleware2);

      return app.processRequest(request).then(() => {
        expect(Response).to.have.calledOnce;
      });
    });

    it('should execute each route with request in the order declared', () => {
      return app.processRequest(request).then(() => {
        expect(middleware1.execute).to.have.been.calledOnce;
        expect(middleware1.execute.firstCall.args[0].request).to.equal(request);
        expect(middleware2.execute).to.have.been.calledOnce;
        expect(middleware2.execute.firstCall.args[0].request).to.equal(request);

        expect(middleware1.execute).to.have.been.calledBefore(middleware2.execute);
      });
    });

    it('should not execute the second middleware before the first one completes', () => {
      middleware1.execute.returnsPromise();
      middleware1.execute.rejects(new Error());

      return app.processRequest(request).catch(() => {
        expect(middleware1.execute).to.have.been.called;
        expect(middleware2.execute).to.not.have.been.called;
      });
    });

    it('should not execute error middleware if all middleware resolves', () => {
      return app.processRequest(request).then(() => {
        expect(errMiddleware1.execute).to.not.have.been.called;
        expect(errMiddleware2.execute).to.not.have.been.called;
      });
    });

    it('should resolve with the created response object', () => {
      return app.processRequest(request).then((response) => {
        expect(response).to.be.instanceof(Response);
      });
    });

    it('should bail to the error stack if a middleware throws an error', () => {
      const err = new Error('uhoh');
      middleware1.execute.throws(err);

      return app.processRequest(request).then(() => {
        expect(errMiddleware1.execute).to.have.been.calledOnce;
        expect(errMiddleware1.execute.firstCall.args[0].error).to.equal(err);
        expect(errMiddleware2.execute).to.not.have.been.calledOnce;
      });
    });

    it('should propagate down the error stack if a previous error middleware throws an error', () => {
      const err1 = new Error('uhoh');
      const err2 = new Error('ohno');
      middleware1.execute.throws(err1);
      errMiddleware1.execute.throws(err2);

      return app.processRequest(request).then(() => {
        expect(errMiddleware1.execute).to.have.been.calledOnce;
        expect(errMiddleware1.execute.firstCall.args[0].error).to.equal(err1);
        expect(errMiddleware2.execute).to.have.been.calledOnce;
        expect(errMiddleware2.execute.firstCall.args[0].error).to.equal(err2);
      });
    });

    it('should bail to the error stack if a middleware rejects with an error', () => {
      const err = new Error('uhoh');
      middleware1.execute.returnsPromise();
      middleware1.execute.rejects(err);

      return app.processRequest(request).then(() => {
        expect(errMiddleware1.execute).to.have.been.calledOnce;
        expect(errMiddleware1.execute.firstCall.args[0].error).to.equal(err);
        expect(errMiddleware2.execute).to.not.have.been.calledOnce;
      });
    });

    it('should propagate down the error stack if a previous error middleware rejects with an error', () => {
      const err1 = new Error('uhoh');
      const err2 = new Error('ohno');
      middleware1.execute.throws(err1);
      errMiddleware1.execute.returnsPromise();
      errMiddleware1.execute.rejects(err2);

      return app.processRequest(request).then(() => {
        expect(errMiddleware1.execute).to.have.been.calledOnce;
        expect(errMiddleware1.execute.firstCall.args[0].error).to.equal(err1);
        expect(errMiddleware2.execute).to.have.been.calledOnce;
        expect(errMiddleware2.execute.firstCall.args[0].error).to.equal(err2);
      });
    });

    it('should not allow a path specific error middleware to swallow a valid error condition', () => {
      app            = new App();

      app.use(middleware1);
      errMiddleware1 = new Route({
        path    : '/a/b/c',
        method  : 'get',
        handler : sinon.spy()
      });
      app.useError(errMiddleware1);
      app.useError(errMiddleware2);

      const err1 = new Error('uhoh');
      middleware1.execute.throws(err1);
      return app.processRequest(request).then(() => {
        expect(errMiddleware1.handler).to.not.have.been.called;
        expect(errMiddleware2.execute).to.have.been.calledOnce;
      });
    });

    it('should reject if all error middleware throw', () => {
      const err1 = new Error('uhoh');
      const err2 = new Error('ohno');
      const err3 = new Error('nodearlord');
      middleware1.execute.throws(err1);
      errMiddleware1.execute.throws(err2);
      errMiddleware2.execute.throws(err3);

      return app.processRequest(request).catch((e) => {
        expect(e).to.equal(err3);
      });
    });
  });
});

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');
const PassThrough       = require('stream').PassThrough;
const Transform         = require('stream').Transform;

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect = chai.expect;

const Request = require('./..').Request;
const Response = require('./..').Response;
const ContentNegotiation = require('./../lib/middleware/ContentNegotiation.js');

describe('Content Negotiation Middleware', () => {
  describe('exports', () => {
    it('export a middleware function for request', () => {
      expect(ContentNegotiation.request).to.be.a('function');
    });

    it('export a middleware function for response', () => {
      expect(ContentNegotiation.response).to.be.a('function');
    });
  });

  describe('request', () => {

    let request;

    describe('buffering', () => {
      beforeEach(() => {
        request = new Request({
          path    : '/',
          method  : 'get',
          headers : {},
          body    : new PassThrough()
        });
      });

      it('if there is no Content-Type, it should try to parse as json and overwrite body', (done) => {
        const requestData = { hello : 'world' };
        request.body.end(JSON.stringify(requestData));
        ContentNegotiation.request({ request }).then(() => {
          expect(request.body).to.eql(requestData);
          done();
        });

      });

      it('should throw an error if parsing fails', (done) => {
        request.body.end('this is not json');
        ContentNegotiation.request({ request }).catch((error) => {
          expect(error.message).to.equal('failed to parse json in request');
          done();
        });
      });

      it('should set the body to null if nothing is streamed in', (done) => {
        request.body.end();
        ContentNegotiation.request({ request }).then(() => {
          expect(request.body).to.be.null;
          done();
        });

      });

    });
    describe('non-buffering', () => {

      beforeEach(() => {
        const headers = {};
        headers['Transfer-Encoding'] = 'chunked';
        request = new Request({
          path    : '/',
          method  : 'get',
          headers : headers,
          body    : new PassThrough()
        });
      });

      it('should do nothing to the response stream if content-type is multipart', (done) => {
        request.body.end('chunked data');
        ContentNegotiation.request({ request });
        expect(request.body instanceof Transform).to.be.true;
        request.body.on('data', (chunk) => {
          expect(chunk.toString()).to.equal('chunked data');
          done();
        });
      });

    });

  });

  describe('response', () => {
    let request;
    let response;

    beforeEach(() => {
      request = new Request({
        path    : '/',
        method  : 'get',
        headers : {},
        body    : new PassThrough()
      });

      response = new Response();
    });

    describe('JSON', () => {
      it('should treat the response body as JSON and write it as a new stream if no headers are present', (done) => {
        response.setBody({ hello : 'world' });
        ContentNegotiation.response({ request, response });
        response.body.on('data', (chunk) => {
          expect(chunk.toString()).to.equal('{"hello":"world"}');
          expect(response.getHeader('content-type')).to.equal('application/json');
          done();
        });
      });

    });

  });
});

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');
const _                 = require('lodash');

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect = chai.expect;

const Node    = require('../lib/Node');
const Route   = require('../lib/Route');
const Request = require('../lib/Request');
let sandbox;

describe('Node ', () => {
  const OPTIONS_ERROR    = 'options must be an object.';
  const PATH_ERROR       = 'path must be a string.';
  const PATH_START_ERROR = 'path must start with a / character.';

  beforeEach(() => {
    sandbox = sinon.sandbox.create();
  });

  afterEach(() => {
    sandbox.restore();
  });

  describe('constructor', () => {
    it('should throw if options is undefined', () => {
      expect(() => {
        new Node();
      }).to.throw(OPTIONS_ERROR);
    });

    it('should throw if options is not an object', () => {
      expect(() => {
        new Node('asdf');
      }).to.throw(OPTIONS_ERROR);
    });

    it('should throw if options.path is undefined', () => {
      expect(() => {
        new Node({});
      }).to.throw(PATH_ERROR);
    });

    it('should throw if options.path is not a string', () => {
      expect(() => {
        new Node({ path : 7 });
      }).to.throw(PATH_ERROR);
    });

    it('should throw if path does not start with a / character', () => {
      expect(() => {
        new Node({ path : 'asdf' });
      }).to.throw(PATH_START_ERROR);
    });

    it('should set the path property from the constructor', () => {
      const node = new Node({ path : '/user' });
      expect(node.path).to.equal('/user');
    });

    it('should throw on assignment to path', () => {
      const node = new Node({ path : '/user' });
      expect(() => {
        node.path = '/asdfa';
      }).to.throw('Cannot set property');
    });

    it('should add an options route', () => {
      const node = new Node({ path : '/user' });
      expect(node.getRoutes()).to.have.length(1);
      const optsRoute = node.getRoutes()[0];
      expect(optsRoute.method).to.equal('OPTIONS');
    });
  });

  describe('methods', () => {
    let node;
    let route;

    beforeEach(() => {
      route = {
        path    : '/user',
        method  : 'get',
        handler : () => {

        }
      };
      node  = new Node({ path : '/user' });
    });

    describe('#_optionsHandler', () => {

    });

    describe('#addRoute', () => {
      it('should throw if the route path does not match the node path pattern', () => {

      });

      it('should throw if the path of the route does not match the path of the node', () => {
        route.path = '/junk';
        expect(() => {
          node.addRoute(route);
        }).to.throw('route path must match node path.');
      });

      it('should throw if the method is already defined for this node', () => {
        node.addRoute(route);
        expect(() => {
          node.addRoute(route);
        }).to.throw('duplicate method "GET" added for path "/user"');
      });

      it('should throw if the route method is ALL', () => {
        route.method = '*';

        expect(() => {
          node.addRoute(route);
        }).to.throw('cannot register route with method ALL on node.');
      });
    });

    describe('#addChild', () => {

      it('should be able to add children and be returned with getChildren', () => {
        const child1 = new Node({ path : '/child1' });
        const child2 = new Node({ path : '/child2' });
        const child3 = new Node({ path : '/child3' });
        node.addChild(child1);
        node.addChild(child2);
        node.addChild(child3);
        expect(node.getChildren()).to.eql([child1, child2, child3]);
      });

      it('should throw an error if a child with a duplicate path is added', () => {
        const child = new Node({ path : '/child' });
        node.addChild(child);
        expect(() => {
          node.addChild(child);
        }).to.throw(`Cannot add duplicate child on path ${child.path}`);
      });

    });

    describe('tree walkers', () => {
      let root, a, az, abc, abd;
      beforeEach(() => {
        root = new Node({ path : '/' });
        a    = new Node({ path : '/a' });
        abc  = new Node({ path : '/a/b/c' });
        abd  = new Node({ path : '/a/b/d' });
        az   = new Node({ path : '/a/z' });

        root.addChild(a);
        a.addChild(az);
        a.addChild(abc);
        a.addChild(abd);
      });

      describe('#insert', () => {

        it('should throw an error if the route path is not contained in the nodes path', () => {
          const route = new Route({
            path    : '/',
            method  : 'get',
            handler : sinon.spy()
          });

          expect(() => {
            abc.insert(route);
          }).to.throw('Cannot insert');
        });

        it('should throw an error if the node path does not start the route path', () => {
          const route = new Route({
            path    : '/b/c',
            method  : 'get',
            handler : sinon.spy()
          });

          expect(() => {
            abd.insert(route);
          }).to.throw('Cannot insert');
        });

        it('should add the route to the node if the paths match', () => {
          sandbox.spy(root, 'addRoute');
          const route = new Route({
            path    : '/',
            method  : 'get',
            handler : sinon.spy()
          });
          root.insert(route);

          expect(root.addRoute).to.have.been.calledOnce;
          expect(root.addRoute).to.have.been.calledWith(route);
        });

        it('should insert on a child node, if possible', () => {
          const route = new Route({
            path    : '/a/b/c/e',
            method  : 'get',
            handler : sinon.spy()
          });
          sinon.spy(a, 'insert');
          sinon.spy(abc, 'insert');
          sinon.spy(abd, 'insert');

          root.insert(route);
          expect(a.insert).to.have.been.calledWith(route);
          expect(abc.insert).to.have.been.calledWith(route);
          expect(abd.insert).to.not.have.been.calledWith(route);
        });

        it('should add a new child otherwise', () => {
          const route   = new Route({
            path    : '/a/b',
            method  : 'get',
            handler : sinon.spy()
          });
          sinon.spy(a, 'insert');
          sinon.spy(a, 'addChild');

          root.insert(route);
          expect(a.insert).to.have.been.calledWith(route);
          expect(a.addChild).to.have.been.calledOnce;
          const newNode = a.addChild.firstCall.args[0];
          expect(newNode.path).to.equal('/a/b');
          expect(newNode.getRoutes()).to.contain(route);
        });

        it('should reassign children as needed', () => {
          const route  = new Route({
            path    : '/a/b',
            method  : 'get',
            handler : sinon.spy()
          });

          root.insert(route);
          expect(root.getChildren()).to.eql([a]);
          const aChildren = a.getChildren();
          expect(aChildren).to.have.length(2);
          expect(aChildren).to.contain(az);
          const ab = _.find(aChildren, (child) => {
            return child !== az;
          });
          expect(ab.getChildren()).to.eql([abc, abd]);
        });

        it('should insert correctly on route patterns, regardless of param names', () => {
          const node = new Node({ path : '/users/:id' });
          const route1 = new Route({
            path    : '/users/:id',
            method  : 'get',
            handler : sinon.spy()
          });
          const route2 = new Route({
            path    : '/users/:userId',
            method  : 'put',
            handler : sinon.spy()
          });
          node.insert(route1);
          node.insert(route2);

          expect(node.getRoutes()).to.contain(route1);
          expect(node.getRoutes()).to.contain(route2);
        });
      });


      describe('#find', () => {
        let rootGet, aGet;
        beforeEach(() => {
          rootGet = new Route({
            path    : '/',
            method  : 'get',
            handler : sinon.spy()
          });
          root.addRoute(rootGet);

          aGet = new Route({
            path    : '/a',
            method  : 'get',
            handler : sinon.spy()
          });
          a.addRoute(aGet);
        });

        it('should return a match from its routes, if it exists', () => {
          const request = new Request({
            path    : '/',
            method  : 'get',
            headers : {}
          });

          expect(root.find(request)).to.equal(rootGet);
        });

        it('should return undefined if request matches the path, but the route doesnt exist', () => {
          const request = new Request({
            path    : '/',
            method  : 'post',
            headers : {}
          });

          expect(root.find(request)).to.be.null;
        });

        it('should return a match from its children', () => {
          const request = new Request({
            path    : '/a',
            method  : 'get',
            headers : {}
          });

          expect(root.find(request)).to.equal(aGet);
        });
      });

      describe('#toObject', () => {

      });
    });
  });
});

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect = chai.expect;

const Request = require('./..').Request;
let request;

describe('Request', () => {

  describe('constructor', () => {
    const OPTIONS_ERROR = 'options must be an object.';
    const HEADERS_ERROR = 'headers must be an object.';
    const PATH_ERROR    = 'path must be a string.';
    const METHOD_ERROR  = 'method must be a valid method string.';
    const BODY_ERROR    = 'body must be a readable stream.';

    it('should throw if options is undefined', () => {
      expect(() => {
        new Request();
      }).to.throw(OPTIONS_ERROR);
    });

    it('should throw if options is not an object', () => {
      expect(() => {
        new Request('asdf');
      }).to.throw(OPTIONS_ERROR);
    });

    it('should not throw if options.headers is undefined', () => {
      expect(() => {
        new Request({});
      }).to.not.throw(HEADERS_ERROR);
    });

    it('should throw if options.headers is defined and not an object', () => {
      expect(() => {
        new Request({ headers : 'asdf' });
      }).to.throw(HEADERS_ERROR);
    });

    it('should throw if options.path is undefined', () => {
      expect(() => {
        new Request({ headers : {} });
      }).to.throw(PATH_ERROR);
    });

    it('should throw if options.path is not a string', () => {
      expect(() => {
        new Request({ headers : {}, path : 7 });
      }).to.throw(PATH_ERROR);
    });

    it('should throw if options.method is undefined', () => {
      expect(() => {
        new Request({ headers : {}, path : '/' });
      }).to.throw(METHOD_ERROR);
    });

    it('should throw if options.method is not a string', () => {
      expect(() => {
        new Request({ headers : {}, path : '/', method : 7 });
      }).to.throw(METHOD_ERROR);
    });

    it('should throw if options.method is not an allowed method value', () => {
      expect(() => {
        new Request({ headers : {}, path : '/', method : 'heart!' });
      }).to.throw(METHOD_ERROR);
    });

    it('should not throw if options.body is undefined', () => {
      expect(() => {
        new Request({ headers : {}, path : '/', method : 'get' });
      }).to.not.throw();
    });

    it('should throw if options.body is defined but not a readable stream', () => {
      expect(() => {
        new Request({ headers : {}, path : '/', method : 'get', body : 'asdf' });
      }).to.throw(BODY_ERROR);
    });

    it('should allow arbitrary properties from constructor', () => {
      const request = new Request({ headers : {}, path : '/', method : 'get', a : 1 });
      expect(request.a).to.equal(1);
    });

    it('should allow construction from an existing request instance', () => {
      const req1 = new Request({ headers : { 'b' : 'c' }, path : '/', method : 'get', a : 1 });
      const req2 = new Request(req1);

      expect(req2.headers).to.eql({ 'b' : 'c' });
      expect(req2.path).to.equal('/');
      expect(req2.method).to.equal('GET');
      expect(req2.a).to.equal(1);
    });
  });

  describe('properties', () => {
    beforeEach(() => {
      request = new Request({
        headers : {
          hello : 'world',
          HeY   : 'BuDdY'
        },
        path    : '/',
        method  : 'get'
      });
    });

    it('should set properties based on constructed values', () => {
      expect(request.getHeader('hello')).to.equal('world');
      expect(request.path).to.equal('/');
      expect(request.method).to.equal('GET');
    });

    it('should defensively copy to protect itself from upstream changes on the headers object', () => {
      const headers = {};
      request       = new Request({
        headers : headers,
        path    : '/',
        method  : 'get'
      });

      headers.a = 1;

      expect(request.getHeader('a')).to.eql(undefined);
    });

    it('should throw if an invalid path is set', () => {
      expect(() => {
        request.path = 7;
      }).to.throw('path must be a string.');
    });

    it('should normalize path when set', () => {
      request.path = '/WoNkY/';
      expect(request.path).to.equal('/wonky');
    });

    it('should throw if an invalid method is set', () => {
      expect(() => {
        request.method = 'adsf';
      }).to.throw('method must be a valid method string.');
    });

    it('should normalize method when set', () => {
      request.method = 'get';
      expect(request.method).to.equal('GET');
    });

    it('should expose an originalPath property', () => {
      expect(request.originalPath).to.exist;
    });

    it('should throw on assignment to originalPath', () => {
      expect(() => {
        request.originalPath = '/asdf';
      }).to.throw('Cannot set property');
    });

    it('should initialize originalPath to the value of path', () => {
      expect(request.originalPath).to.equal('/');
    });

    it('should initialize originalPath to the input originalPath, if provided', () => {
      request.path = '/a/b/c';
      const newReq = new Request(request);
      expect(newReq.originalPath).to.equal('/');
    });

    it('should allow the body property to be overwritten', () => {
      request.body = { hello : 'world' };
      expect(request.body).to.eql({ hello : 'world' });
    });

    it('should allow assignment of arbitrary properties', () => {
      request.asdf = {};
      expect(request.asdf).to.eql({});
    });

    it('getting headers should not be case sensitive', () => {
      expect(request.getHeader('HeLlO')).to.equal('world');
    });

    it('header values case should be preserved', () => {
      expect(request.getHeader('hey')).to.equal('BuDdY');
    });

  });

  describe('streaming body', () => {
    beforeEach(() => {
      request = new Request({ path : '/', method : 'get' });
    });

    it('should be an instance of transform stream', () => {
      expect(request.body).to.be.instanceof(require('stream').Transform);
    });

    it('should be readable and writable', (done) => {
      const PassThrough = require('stream').PassThrough;
      const inStream    = new PassThrough();
      const outStream   = new PassThrough();

      inStream.pipe(request.body).pipe(outStream);

      inStream.end('hello world');
      outStream.on('data', (chunk) => {
        expect(chunk.toString()).to.equal('hello world');
        done();
      });
    });
  });

});

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');
const PassThrough       = require('stream').PassThrough;
const Transform         = require('stream').Transform;

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect = chai.expect;

const Response = require('./..').Response;
let response;

describe('Response', () => {

  beforeEach(() => {
    response = new Response();
  });

  describe('statusCode', () => {
    it('should default to 200', () => {
      expect(response.statusCode).to.equal(200);
    });

    it('should throw an error if assigned a non-numeric value', () => {
      expect(() => {
        response.statusCode = 'asdf';
      }).to.throw('statusCode must be a number.');
    });

    it('should be assignable', () => {
      response.statusCode = 500;
      expect(response.statusCode).to.equal(500);
    });
  });

  describe('headers', () => {
    it('should return undefined for an undefined header', () => {
      expect(response.getHeader('asdf')).to.be.undefined;
    });

    it('should throw if set key is not defined', () => {
      expect(() => {
        response.setHeader();
      }).to.throw('First argument: key must be a string.');
    });

    it('should throw if set key is not a string', () => {
      expect(() => {
        response.setHeader(7);
      }).to.throw('First argument: key must be a string.');
    });

    it('should throw if set value is not defined', () => {
      expect(() => {
        response.setHeader('Content-Type');
      }).to.throw('Second argument: value must be a string.');
    });

    it('should throw if set value is not a string', () => {
      expect(() => {
        response.setHeader('Content-Type', 7);
      }).to.throw('Second argument: value must be a string.');
    });

    it('should be settable', () => {
      response.setHeader('Content-Type', 'application/json');
      expect(response.getHeader('Content-Type')).to.equal('application/json');
    });

    it('should allow clearing of headers', () => {
      response.setHeader('Content-Type', 'application/json');
      expect(response.getHeader('Content-Type')).to.equal('application/json');
      response.clearHeader('Content-Type');
      expect(response.getHeader('Content-Type')).to.be.undefined;
    });
  });

  describe('body', () => {
    let inStream;
    let outStream;

    beforeEach(() => {
      inStream  = new PassThrough();
      outStream = new PassThrough();
    });

    describe('property', () => {
      beforeEach(() => {
        inStream.pipe(response.body).pipe(outStream);
      });

      it('should extend Transform stream', () => {
        expect(response.body).to.be.instanceof(Transform);
      });

      it('should be readable and writable', (done) => {
        inStream.end('hello world');
        outStream.on('data', (chunk) => {
          expect(chunk.toString()).to.equal('hello world');
          done();
        });
      });

      it('should throw if you assign to body', () => {
        expect(() => {
          response.body = 'asdf';
        }).to.throw('Cannot set property');
      });
    });

    describe('#setBody', () => {

      it('should pipe if the input is a readable stream', (done) => {
        inStream.end('hello world');

        response.setBody(inStream);
        response.body.pipe(outStream);

        outStream.on('data', (chunk) => {
          expect(chunk.toString()).to.equal('hello world');
          done();
        });
      });

      it('should .end to itself with the input value otherwise', (done) => {
        response.setBody('goodbye cruel world');
        response.body.pipe(outStream);

        outStream.on('data', (chunk) => {
          expect(chunk.toString()).to.equal('goodbye cruel world');
          done();
        });
      });

      it('should break piping from previous sources', (done) => {
        inStream.end('hello world');
        inStream.pipe(response.body);

        response.setBody('goodbye cruel world');
        response.body.pipe(outStream);

        outStream.on('data', (chunk) => {
          expect(chunk.toString()).to.equal('goodbye cruel world');
          done();
        });
      });
    });

    describe('#getBody', () => {
      it('should return a reference to the body stream after construction', () => {
        response = new Response();

        expect(response.getBody()).to.equal(response.body);
      });

      it('should return a reference to the body stream after we setBody to a readable stream ', () => {
        response.setBody(inStream);
        expect(response.getBody()).to.equal(response.body);
      });

      it('should return a reference to the assigned balue if we setBody to a non-stream value', () => {
        response.setBody('hello world');
        expect(response.getBody()).to.equal('hello world');
      });
    });

  });


});

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');
const _                 = require('lodash');
const Q                 = require('q');

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect = chai.expect;

const Route    = require('../lib/Route');
const Request  = require('../lib/Request');
const Response = require('../lib/Response');

describe('A Route', () => {
  let opts;
  let route;
  let handler;

  beforeEach(() => {
    handler = sinon.stub();
    opts    = { path : '/', method : 'GET', handler : handler };
    route   = new Route(opts);
  });

  describe('constructor', () => {
    const OPTIONS_ERROR = 'options must be an object.';
    const PATH_ERROR    = 'path must be a string.';
    const METHOD_ERROR  = 'method must be a valid method string.';
    const HANDLER_ERROR = 'handler must be a function.';

    it('should throw if options is undefined', () => {
      expect(() => {
        new Route();
      }).to.throw(OPTIONS_ERROR);
    });

    it('should throw if options is not an object', () => {
      expect(() => {
        new Route('asdf');
      }).to.throw(OPTIONS_ERROR);
    });

    it('should throw if handler is not defined;', () => {
      expect(() => {
        new Route({});
      }).to.throw(HANDLER_ERROR);
    });

    it('should throw if handler is not a function', () => {
      expect(() => {
        new Route({ handler : 'asdf' });
      }).to.throw(HANDLER_ERROR);
    });

    it('should not throw if options.path is undefined', () => {
      expect(() => {
        new Route({ handler : sinon.spy() });
      }).to.not.throw();
    });

    it('should throw if options.path is not a string', () => {
      expect(() => {
        new Route({ handler : sinon.spy(), path : 7 });
      }).to.throw(PATH_ERROR);
    });

    it('should not throw if options.method is undefined', () => {
      expect(() => {
        new Route({ handler : sinon.spy() });
      }).to.not.throw();
    });

    it('should throw if options.method is not a string', () => {
      expect(() => {
        new Route({ handler : sinon.spy(), method : 7 });
      }).to.throw(METHOD_ERROR);
    });

    it('should throw if options.method is not an allowed method value', () => {
      expect(() => {
        new Route({ handler : sinon.spy(), method : 'heart!' });
      }).to.throw(METHOD_ERROR);
    });

  });

  describe('properties', () => {
    _.each(['path', 'method', 'handler'], (propertyName) => {
      it(`should set property ${propertyName} from constructor`, () => {
        expect(route[propertyName]).to.equal(opts[propertyName]);
      });

      it(`should throw on assignment to ${propertyName}`, () => {
        expect(() => {
          route[propertyName] = 'a';
        }).to.throw('Cannot set property');
      });
    });

    it('should normalize root path', () => {
      route = new Route({ path : '/', method : 'get', handler : handler });
      expect(route.path).to.equal('/');
    });

    it('should normalize trailing slashes', () => {
      route = new Route({ path : '/a/b/c/', method : 'get', handler : handler });
      expect(route.path).to.equal('/a/b/c');
    });

    it('should normalize trailing slashes', () => {
      route = new Route({ path : '/a/b/c', method : 'get', handler : handler });
      expect(route.path).to.equal('/a/b/c');
    });

    it('should lowercase path parts that are NOT route params', () => {
      route = new Route({ path : '/CaSes/:caseId/THINg', method : 'get', handler : handler });
      expect(route.path).to.equal('/cases/:caseId/thing');
    });

    it('should normalize method name', () => {
      route = new Route({ path : '/a/b/c', method : 'GeT', handler : handler });
      expect(route.method).to.equal('GET');
    });

    it('should default method to ALL', () => {
      route = new Route({ path : '/a/b/c', handler : handler });
      expect(route.method).to.equal('*');
    });

    it('should default path to all', () => {
      route = new Route({ handler : handler });
      expect(route.path).to.equal('*all');
    });

    it('should extend with arbitrary parameters', () => {
      route = new Route(_.extend({}, opts, { a : 1, b : 'AHHH' }));

      expect(route.a).to.equal(1);
      expect(route.b).to.equal('AHHH');
    });

    it('should not allow arbitrary parameters to overwrite internal state', () => {
      route = new Route(_.extend({}, opts, { _path : 'okthen?' }));

      expect(route.path).to.equal('/');
    });
  });

  describe('isMatch', () => {
    it('should throw if the input is not a request object', () => {
      expect(() => {
        route.isMatch({});
      }).to.throw('First argument: request must be a SuperRouter Request instance.');
    });

    it('should return true if a request is an exact match for the route', () => {
      const req = new Request({
        method  : 'get',
        path    : '/',
        headers : {}
      });

      expect(route.isMatch(req)).to.be.true;
    });

    it('should return true for any method if the route is to match all methods', () => {
      route = new Route({
        method  : '*',
        path    : '/',
        handler : sinon.spy()
      });

      const req = new Request({
        method  : 'get',
        path    : '/',
        headers : {}
      });

      expect(route.isMatch(req)).to.be.true;
    });

    it('should return true for any method if the route is to match all paths', () => {
      route = new Route({
        method  : 'get',
        path    : '*all',
        handler : sinon.spy()
      });

      const req = new Request({
        method  : 'get',
        path    : '/a/b/c',
        headers : {}
      });

      expect(route.isMatch(req)).to.be.true;
    });

    it('should return false if a the method is wrong', () => {
      const req = new Request({
        method  : 'put',
        path    : '/',
        headers : {}
      });

      expect(route.isMatch(req)).to.be.false;
    });

    it('should return false if a the path is wrong', () => {
      const req = new Request({
        method  : 'get',
        path    : '/a/b',
        headers : {}
      });

      expect(route.isMatch(req)).to.be.false;
    });
  });

  describe('#execute', () => {
    let request;
    let response;

    it('should throw if options is not defined', () => {
      expect(() => {
        route.execute();
      }).to.throw('options must be an object.');
    });

    it('should throw if options is not an object', () => {
      expect(() => {
        route.execute(7);
      }).to.throw('options must be an object.');
    });

    it('should throw if the input is not a request object', () => {
      expect(() => {
        route.execute({});
      }).to.throw('request must be a SuperRouter Request instance.');
    });

    it('should throw if the second input is not a response object', () => {
      request = new Request({
        method  : 'get',
        path    : '/a/b',
        headers : {}
      });

      expect(() => {
        route.execute({ request });
      }).to.throw('response must be a SuperRouter Response instance.');
    });

    describe('on non-match', () => {
      beforeEach(() => {
        request  = new Request({
          method  : 'get',
          path    : '/a/b',
          headers : {}
        });
        response = new Response();
      });

      it('should return a promise', () => {
        expect(Q.isPromise(route.execute({ request, response }))).to.be.true;
      });

      it('should resolve the promise without executing the handler', () => {
        return route.execute({ request, response }).then(() => {
          expect(handler).to.not.have.been.called;
        });
      });

      it('should pass properties on options to handler', () => {
        const opts = {
          request  : new Request({
            method  : 'get',
            path    : '/',
            headers : {}
          }),
          response : new Response(),
          a        : 1,
          b        : 'thing'
        };

        return route.execute(opts).then(() => {
          expect(handler).to.have.been.calledWith(opts);
        });
      });

    });

    describe('on match', () => {
      beforeEach(() => {
        request  = new Request({
          method  : 'get',
          path    : '/',
          headers : {}
        });
        response = new Response();
      });

      it('should return a promise', () => {
        expect(Q.isPromise(route.execute({ request, response }))).to.be.true;
      });

      it('should resolve the promise, executing the handler if the request is a match', () => {
        return route.execute({ request, response }).then(() => {
          expect(handler).to.have.been.calledOnce;
        });
      });

      it('should pass the request and response to the handler', () => {
        return route.execute({ request, response }).then(() => {
          expect(handler).to.have.been.calledWith({ request, response });
        });
      });

      it('should catch any thrown errors and reject the promise with them', () => {
        const err = new Error('A TERRIBLE TRAGEDY');
        handler.throws(err);

        return route.execute({ request, response }).catch((thrownErr) => {
          expect(thrownErr).to.equal(err);
        });
      });

      it('should resolve with the return value if the handler does not return a promise', () => {
        handler.returns('resolveVal');

        return route.execute({ request, response }).then((res) => {
          expect(res).to.equal('resolveVal');
        });
      });

      it('should resolve if the handler resolves', () => {
        handler.returnsPromise();
        handler.resolves('resolveVal');

        return route.execute({ request, response }).then((res) => {
          expect(res).to.equal('resolveVal');
        });
      });

      it('should reject if the handler rejects', () => {
        handler.returnsPromise();
        const err = new Error('A TERRIBLE TRAGEDY');
        handler.rejects(err);

        return route.execute({ request, response }).catch((thrownErr) => {
          expect(thrownErr).to.equal(err);
        });
      });

      it('should propagate mutations on the request and response that occur within the handler', () => {
        route = new Route({
          path    : '/',
          method  : 'get',
          handler : (opts) => {
            const req = opts.request;
            const res = opts.response;

            req.a = 1;
            res.b = 2;
          }
        });

        return route.execute({ request, response }).then(() => {
          expect(request.a).to.equal(1);
          expect(response.b).to.equal(2);
        });
      });

      describe('routeParams', () => {
        beforeEach(() => {
          route = new Route({
            path    : '/user(/:type)/:id(/*rest)',
            method  : 'get',
            handler : sinon.spy()
          });
        });

        it('should create a routeParams property on the request', () => {
          request = new Request({
            path    : '/',
            method  : 'get',
            headers : {}
          });
          route.execute({ request, response });
          expect(request.routeParams).to.exist;
          expect(request.routeParams).to.eql({});
        });

        it('should overwrite an existing routeParams property', () => {
          request             = new Request({
            path    : '/',
            method  : 'get',
            headers : {}
          });
          request.routeParams = { a : 1 };
          route.execute({ request, response });
          expect(request.routeParams).to.exist;
          expect(request.routeParams).to.eql({});
        });

        it('should represent each match from the route path on the routeParams property', () => {
          request = new Request({
            path    : '/user/red/17/a/b/c',
            method  : 'get',
            headers : {}
          });
          route.execute({ request, response });
          expect(request.routeParams).to.exist;
          expect(request.routeParams).to.eql({
            type : 'red',
            id   : '17',
            rest : 'a/b/c'
          });
        });
      });
    });
  });
});

'use strict';
const chai              = require('chai');
const sinon             = require('sinon');
const sinonChai         = require('sinon-chai');
const sinonStubPromises = require('sinon-promises');
const chaiAsPromised    = require('chai-as-promised');
const proxyquire        = require('proxyquire');

sinonStubPromises(sinon);
chai.use(chaiAsPromised);
chai.use(sinonChai);

const expect  = chai.expect;
const sandbox = sinon.sandbox.create();

const Route    = require('../lib/Route');
const Request  = require('../lib/Request');
const Response = require('../lib/Response');

let Router;
let router;
let mockTree;
let request;
let response;

describe('Router ', () => {
  beforeEach(() => {
    delete require.cache[require.resolve('../lib/Router')];

    const RouteTree = require('../lib/RouteTree');
    mockTree        = sinon.createStubInstance(RouteTree);

    Router = proxyquire('../lib/Router', {
      './RouteTree' : sinon.stub().returns(mockTree)
    });
    router = new Router();
  });

  afterEach(() => {
    sandbox.restore();
  });

  describe('#addRoute', () => {
    it('should construct a new Route object from the input', () => {
      const mockRoute = sinon.stub();
      mockRoute.returns({
        path : ''
      });
      Router          = proxyquire('../lib/Router', {
        './Route'     : mockRoute,
        './RouteTree' : sinon.stub().returns(mockTree)
      });

      const opts = {};
      router     = new Router();
      router.addRoute(opts);
      expect(mockRoute).to.have.been.calledOnce;
      expect(mockRoute).to.have.been.calledWithNew;
      expect(mockRoute).to.have.been.calledWith(opts);
    });

    it('should add the new route to its tree', () => {
      const route = new Route({
        path    : '/',
        method  : 'get',
        handler : sinon.spy()
      });
      router.addRoute(route);

      expect(mockTree.addRoute).to.have.been.calledOnce;
      expect(mockTree.addRoute).to.have.been.calledWith(route);
    });

    it('should throw an error if the route contains a splat', () => {
      expect(() => {
        router.addRoute({
          path    : '/*start/foo',
          method  : 'get',
          handler : sinon.spy()
        });
      }).to.throw('Splats and optional groups are not supported for routes.');
    });

    it('should throw an error if the route contains an optional', () => {
      expect(() => {
        router.addRoute({
          path    : '(/start)/foo',
          method  : 'get',
          handler : sinon.spy()
        });
      }).to.throw('Splats and optional groups are not supported for routes.');
    });
  });

  describe('middleware', () => {
    beforeEach(() => {
      router = new Router();

      router.addRoute({
        path    : '/user/:id',
        method  : 'post',
        handler : sinon.spy()
      });

      router.addRoute({
        path    : '/user/:id',
        method  : 'get',
        handler : sinon.spy()
      });

      request = new Request({
        path    : '/',
        method  : 'get',
        headers : {}
      });

      response = new Response();
    });

    describe('#match', () => {
      it('should find the route on its routeTree', () => {
        router.match({ request });

        expect(mockTree.find).to.have.been.calledOnce;
        expect(mockTree.find).to.have.been.calledWith(request);
      });

      it('should attach the matched route info to the request', () => {
        const route = new Route({
          path    : '/',
          method  : 'get',
          handler : sinon.spy()
        });

        mockTree.find.returns(route);

        router.match({ request });
        expect(request.matchedRoute).to.equal(route);
      });

      it('should attach an empty match info if there is not matched route', () => {
        mockTree.find.returns(null);

        router.match({ request });
        expect(request.matchedRoute).to.be.null;
      });
    });

    describe('#execute', () => {
      it('should invoke execute on the matched route, if it exists', () => {
        const route = new Route({
          path    : '/',
          method  : 'get',
          handler : sinon.spy()
        });
        sinon.spy(route, 'execute');

        request.matchedRoute = route;
        router.execute({ request, response });

        expect(route.execute).to.have.been.calledOnce;
        expect(route.execute).to.have.been.calledWith({ request, response });
      });

      it('should not throw if the matched route doesnt exist', () => {
        expect(() => {
          router.execute({ request, response });
        }).to.not.throw();
      });
    });
  });
});
